import React, { useState, useEffect, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSun, faMoon, faBars, faCode } from '@fortawesome/free-solid-svg-icons';
import '../css/TaskBar.css';

const Taskbar = ({ toggleMode, modeNuit, handleMenuItemClick }) => {
  const [menuVisible, setMenuVisible] = useState(false);
  const menuRef = useRef(null);

  const toggleMenu = () => {
    setMenuVisible(!menuVisible);
  };

  const closeMenu = () => {
    setMenuVisible(false);
  };

  useEffect(() => {
    const handleDocumentClick = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        closeMenu();
      }
    };

    document.addEventListener('click', handleDocumentClick);

    return () => {
      document.removeEventListener('click', handleDocumentClick);
    };
  }, []);

  return (
    <div className="taskbar">
      <div className='menu_code'>
        <FontAwesomeIcon icon={faCode} size="1x" />
      </div>
      <div className="mode_icon" onClick={toggleMode}>
        <FontAwesomeIcon icon={modeNuit ? faSun : faMoon} size="1x" />
      </div>
      <div className='menu_bars' onClick={toggleMenu}>
        <FontAwesomeIcon icon={faBars} size="1x" />
      </div>
      {menuVisible && (
        <div className="menu-dropdown-float">
        <div className="menu-item" onClick={() => { handleMenuItemClick("Accueil"); closeMenu(); }}>Accueil</div>
        <div className="menu-item" onClick={() => { handleMenuItemClick("Formations"); closeMenu(); }}>Formations</div>
        <div className="menu-item" onClick={() => { handleMenuItemClick("Compétences"); closeMenu(); }}>Compétences</div>
        <div className="menu-item" onClick={() => { handleMenuItemClick("Réalisations"); closeMenu(); }}>Réalisations</div>
        <div className="menu-item" onClick={() => { handleMenuItemClick("A propos"); closeMenu(); }}>A propos</div>
        <div className="menu-item" onClick={() => { handleMenuItemClick("Contacts"); closeMenu(); }}>Contacts</div>
      </div>
      )}
    </div>
  );
};

export default Taskbar;
