import React, { useRef, useEffect } from 'react';
import './App.css';
import Taskbar from './components/TaskBar';
import { motion, useScroll, useSpring } from "framer-motion";

const App = () => {
  const ref = useRef(null);
  const { scrollY } = useScroll({ container: ref });
  const scaleX = useSpring(scrollY, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = ref.current.scrollTop;
      const containerHeight = ref.current.clientHeight;
      const windowHeight = window.innerHeight;
      const currentDivIndex = Math.floor(scrollTop / windowHeight);

      if (currentDivIndex < ref.current.children.length - 1) {
        const nextDivOffset = (currentDivIndex + 1) * windowHeight;
        if (scrollTop + containerHeight >= nextDivOffset) {
          ref.current.children[currentDivIndex + 1].scrollIntoView({ behavior: 'smooth' });
        }
      }
    };

    ref.current.addEventListener('scroll', handleScroll);

    return () => {
      ref.current.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className={`container-1 scrollable-content`} ref={ref}>
      <Taskbar/>
      <motion.div
        className="progress-bar"
        style={{ scaleX }}
      />
      <div className="container-2">
        <h1>Home</h1>
      </div>
      <div className="container-2">
        <h1>Formations</h1>
      </div>
      <div className="container-2">
        <h1>Compétences</h1>
      </div>
      <div className="container-2">
        <h1>Réalisations</h1>
      </div>
      <div className="container-2">
        <h1>A propos</h1>
      </div>
      <div className="container-2">
        <h1>Contact</h1>
      </div>
    </div>
  );
};

export default App;
