import React, { useRef } from 'react';
import './App.css';
import Taskbar from './components/TaskBar';
import { motion, useScroll, useSpring } from "framer-motion";

const App = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ container: ref });
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  return (
    <div className={`container-1 scrollable-content`} ref={ref} style={{ scrollSnapType: 'y mandatory', scrollBehavior: 'smooth' }}>
      <Taskbar/>
      <motion.div
        className="progress-bar"
        style={{ scaleX }}
      />
      <div className="container-2" style={{ scrollSnapAlign: 'start', scrollSnapStop: 'always' }}>
        <h1>home</h1>
      </div>
      <div className="container-2" style={{ scrollSnapAlign: 'start', scrollSnapStop: 'always' }}>
        <h1>Formations</h1>
      </div>
      <div className="container-2" style={{ scrollSnapAlign: 'start', scrollSnapStop: 'always' }}>
        <h1>Compétences</h1>
      </div>
      <div className="container-2" style={{ scrollSnapAlign: 'start', scrollSnapStop: 'always' }}>
        <h1>Réalisations</h1>
      </div>
      <div className="container-2" style={{ scrollSnapAlign: 'start', scrollSnapStop: 'always' }}>
        <h1>A propos</h1>
      </div>
      <div className="container-2" style={{ scrollSnapAlign: 'start', scrollSnapStop: 'always' }}>
        <h1>Contact</h1>
      </div>
    </div>
)};

export default App;
