import React, { useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import Taskbar from './components/TaskBar';
import Home from './components/Home';
import Formations from './components/Formations';
import Competences from './components/Competences';
import { motion, useScroll, useSpring } from "framer-motion";

const App = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ container: ref });
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 80,
    damping: 30,
    restDelta: 0.001
  });

  return (
    <div className={`container-1 scrollable-content`} ref={ref}>
      <Taskbar/>
      <div className="container-2">
        <Home/>
      </div>
      <div className="container-2">
        <Formations/>
      </div>
      <div className="container-2">
        <Competences/>
      </div>
      <div className="container-2">
        <h1>RÃ©alisations</h1>
      </div>
      <div className="container-2">
        <h1>A propos</h1>
      </div>
      <div className="container-2">
        <h1>Contact</h1>
      </div>
      {/* Placer le ProgressBar ici pour qu'il soit au-dessus des autres contenus */}
      <motion.ProgressBar
        className="progress_page"
        style={{ scaleX, backgroundColor: 'darkred', position: 'absolute', left: '50%', transform: 'translateX(-50%)' }}
      />
    </div>
  );
  }  

export default App;
