import React, { useRef, useEffect } from 'react';
import {  CProgress, CProgressBar, CButton } from '@coreui/react'
import '../css/Competences.css';
import Comp_pic from '../assets/images/competence.png'

const Competences = () => {
    const buttonsRef = useRef([]);

    useEffect(() => {
        const interval = setInterval(() => {
          let nextButtonIndex = -1;
          buttonsRef.current.forEach((buttonRef, index) => {
            if (buttonRef.classList.contains('hovered')) {
              buttonRef.classList.remove('hovered');
              nextButtonIndex = index === buttonsRef.current.length - 1 ? 0 : index + 1;
            }
          });
          if (nextButtonIndex !== -1) {
            buttonsRef.current[nextButtonIndex].classList.add('hovered');
          }
        }, 1000); // Délai en millisecondes entre chaque survol

        return () => clearInterval(interval);
    }, []);


    return (
        <div className="comp_container">
            <h2 className="comp_h2">Compétences</h2>
            <div className='comp_comp'>
                <CButton ref={el => el && buttonsRef.current.push(el)} color="light" variant="outline" shape="rounded-pill">Conception</CButton>
                <CButton ref={el => el && buttonsRef.current.push(el)} color="light" variant="outline" shape="rounded-pill">Méthodologie</CButton>
                <CButton ref={el => el && buttonsRef.current.push(el)} color="light" variant="outline" shape="rounded-pill">Front-End</CButton>
                <CButton ref={el => el && buttonsRef.current.push(el)} color="light" variant="outline" shape="rounded-pill">Back-End</CButton>
            </div>
            <div className='comp_item'>
                <CProgress className="comp_progress" color="success" value={10}>
                    <CProgressBar>25%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="info" value={50}>
                    <CProgressBar>50%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="warning" value={75}>
                    <CProgressBar>75%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="danger" value={100}>
                    <CProgressBar>100%</CProgressBar>
                </CProgress>
            </div>
            <img
                src={Comp_pic}
                alt="pic_comp !"
                style={{
                position: 'absolute', 
                top: '60%',           
                right: '28%',         
                transform: 'translateY(-60%)', 
                width: '25%',      
                height: 'auto',       
                }}
            />
        </div>
    );
};

export default Competences;
