import React, { useRef, useEffect, useState } from 'react';
import {  CProgress, CProgressBar, CButton } from '@coreui/react'
import '../css/Competences.css';
import Comp_pic from '../assets/images/competence.png'

const Competences = () => {
    const conceptionButtonRef = useRef(null);
    const methodologieButtonRef = useRef(null);
    const frontendButtonRef = useRef(null);
    const backendButtonRef = useRef(null);
    const [boutonSuivant, setBoutonSuivant] = useState(null);
    const [simulationEnCours, setSimulationEnCours] = useState(true);


    useEffect(() => {
        // Simuler un clic automatique sur le bouton "Conception" après le rendu initial
      simulerClicAutomatique(conceptionButtonRef);
    }, []); 

    useEffect(() => {
      if (boutonSuivant && simulationEnCours) {
        const timeoutId = setTimeout(() => {
          simulerClicAutomatique(boutonSuivant);
        }, 3000); // Délai en millisecondes entre les clics sur les boutons
        return () => clearTimeout(timeoutId);
      }
    }, [boutonSuivant, simulationEnCours]);

    const simulerClicAutomatique = (buttonRef) => {
      if (buttonRef && buttonRef.current && simulationEnCours) {
        buttonRef.current.dispatchEvent(new MouseEvent('click', { bubbles: true }));
        // Ajouter la classe pour l'effet visuel
        buttonRef.current.classList.add('button-clicked');
        setTimeout(() => {
          // Supprimer la classe après un court délai
          buttonRef.current.classList.remove('button-clicked');
        }, 3000); // Durée en millisecondes de l'effet visuel
        // Définir le bouton suivant
        switch (buttonRef) {
          case conceptionButtonRef:
            setBoutonSuivant(methodologieButtonRef);
            break;
          case methodologieButtonRef:
            setBoutonSuivant(frontendButtonRef);
            break;
          case frontendButtonRef:
            setBoutonSuivant(backendButtonRef);
            break;
          case backendButtonRef:
            setBoutonSuivant(conceptionButtonRef); // Revenir au premier bouton
            break;
          default:
            setBoutonSuivant(null);
            break;
        }
      }
    };

    const handleManualClick = () => {
        setSimulationEnCours(true);
    };

    return (
        <div className="comp_container">
            <h2 className="comp_h2">Compétences</h2>
            <div className='comp_comp'>
                <CButton  
                    ref={conceptionButtonRef} 
                    onClick={handleManualClick}  
                    color="light" 
                    variant="outline">
                        Conception
                </CButton>
                <CButton  
                    ref={methodologieButtonRef} 
                    onClick={handleManualClick}  
                    color="light" 
                    variant="outline">
                        Méthodologie
                </CButton>
                <CButton  ref={frontendButtonRef} onClick={handleManualClick}  color="light" variant="outline">Front-End</CButton>
                <CButton  ref={backendButtonRef} onClick={handleManualClick} color="light" variant="outline">Back-End</CButton>
            </div>
            <div className='comp_item'>
                <CProgress className="comp_progress" color="success" value={10}>
                    <CProgressBar>25%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="info" value={50}>
                    <CProgressBar>50%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="warning" value={75}>
                    <CProgressBar>75%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="danger" value={100}>
                    <CProgressBar>100%</CProgressBar>
                </CProgress>
            </div>
            <img
                src={Comp_pic}
                alt="pic_comp !"
                style={{
                position: 'absolute', 
                top: '60%',           
                right: '28%',         
                transform: 'translateY(-60%)', 
                width: '25%',      
                height: 'auto',       
                }}
            />
        </div>
    );
};

export default Competences;
