import React, { useRef, useEffect, useState } from 'react';
import {  CProgress, CProgressBar, CButton } from '@coreui/react'
import '../css/Competences.css';
import Comp_pic from '../assets/images/competence.png'

const Competences = () => {
    const [boutonSuivant, setBoutonSuivant] = useState(null);
    const conceptionButtonRef = useRef(null);
    const methodologieButtonRef = useRef(null);
    const frontendButtonRef = useRef(null);
    const backendButtonRef = useRef(null);

    useEffect(() => {
        // Récupérer l'ID du bouton suivant à partir du stockage local lors du chargement de la page
        const savedButtonId = localStorage.getItem('nextButtonId');
        if (savedButtonId) {
          setBoutonSuivantId(savedButtonId);
        } else {
          // Si aucun ID n'est trouvé, simuler un clic automatique sur le bouton "Conception"
          simulerClicAutomatique(conceptionButtonRef);
        }
      }, []); 

      useEffect(() => {
        // Enregistrer l'ID du bouton suivant dans le stockage local à chaque changement
        if (boutonSuivantId) {
          localStorage.setItem('nextButtonId', boutonSuivantId);
        }
      }, [boutonSuivantId]);

      useEffect(() => {
        if (boutonSuivantId) {
          const buttonRef = getButtonRefById(boutonSuivantId);
          if (buttonRef && buttonRef.current) {
            simulerClicAutomatique(buttonRef);
          }
        }
      }, [boutonSuivantId]);

      const simulerClicAutomatique = (buttonRef) => {
        if (buttonRef && buttonRef.current) {
          buttonRef.current.dispatchEvent(new MouseEvent('click', { bubbles: true }));
          // Ajouter la classe pour l'effet visuel
          buttonRef.current.classList.add('button-clicked');
          setTimeout(() => {
            // Supprimer la classe après un court délai
            buttonRef.current.classList.remove('button-clicked');
          }, 200); // Durée en millisecondes de l'effet visuel
          // Définir l'ID du bouton suivant
          switch (buttonRef) {
            case conceptionButtonRef:
              setBoutonSuivantId('methodologie');
              break;
            case methodologieButtonRef:
              setBoutonSuivantId('frontend');
              break;
            case frontendButtonRef:
              setBoutonSuivantId('backend');
              break;
            case backendButtonRef:
              setBoutonSuivantId('conception'); // Revenir au premier bouton
              break;
            default:
              setBoutonSuivantId(null);
              break;
          }
        }
      };

      const handleClick = (buttonId, message) => {
        // Arrêter la boucle si l'utilisateur interagit avec l'un des boutons
        setBoutonSuivantId(null);
        // Afficher le message dans la console
        console.log(message);
        // Lancer la simulation de clic automatique sur le bouton suivant
        simulerClicAutomatique(getButtonRefById(buttonId));
      };

      const getButtonRefById = (buttonId) => {
        switch (buttonId) {
          case 'conception':
            return conceptionButtonRef;
          case 'methodologie':
            return methodologieButtonRef;
          case 'frontend':
            return frontendButtonRef;
          case 'backend':
            return backendButtonRef;
          default:
            return null;
        }
      };


    return (
        <div className="comp_container">
            <h2 className="comp_h2">Compétences</h2>
            <div className='comp_comp'>
                <CButton  ref={conceptionButtonRef} onClick={() => handleClick(conceptionButtonRef)} color="light" variant="outline">Conception</CButton>
                <CButton  ref={methodologieButtonRef} onClick={() => handleClick(methodologieButtonRef)} color="light" variant="outline">Méthodologie</CButton>
                <CButton  ref={frontendButtonRef} onClick={() => handleClick(frontendButtonRef)} color="light" variant="outline">Front-End</CButton>
                <CButton  ref={backendButtonRef} onClick={() => handleClick(backendButtonRef)} color="light" variant="outline">Back-End</CButton>
            </div>
            <div className='comp_item'>
                <CProgress className="comp_progress" color="success" value={10}>
                    <CProgressBar>25%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="info" value={50}>
                    <CProgressBar>50%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="warning" value={75}>
                    <CProgressBar>75%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="danger" value={100}>
                    <CProgressBar>100%</CProgressBar>
                </CProgress>
            </div>
            <img
                src={Comp_pic}
                alt="pic_comp !"
                style={{
                position: 'absolute', 
                top: '60%',           
                right: '28%',         
                transform: 'translateY(-60%)', 
                width: '25%',      
                height: 'auto',       
                }}
            />
        </div>
    );
};

export default Competences;
