import React, { useEffect, useState } from 'react';
import './App.css';
import { motion, useAnimation } from "framer-motion";
import Taskbar from './components/TaskBar';

const App = () => {
  const controls = useAnimation();
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY >= window.innerHeight) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };

    const controlsHandler = () => {
      if (isVisible) {
        controls.start("visible");
      } else {
        controls.start("hidden");
      }
    };

    window.addEventListener('scroll', handleScroll);
    controlsHandler();

    return () => window.removeEventListener('scroll', handleScroll);
  }, [controls, isVisible]);

  const variants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 },
  };

  return (
    <div className={`container-1 scrollable-content`}>
      <Taskbar/>
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>home</h1>
      </motion.div>
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>Formations</h1>
      </motion.div>
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>Compétences</h1>
      </motion.div>
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>Réalisations</h1>
      </motion.div>
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>A propos</h1>
      </motion.div>
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>Contact</h1>
      </motion.div>
    </div>
  );
}

export default App;
