import React, { useRef, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import Taskbar from './components/TaskBar';
import Home from './components/Home';
import Formations from './components/Formations';
import Competences from './components/Competences';
import Realisations from './components/Realisations';
import Apropos from './components/Apropos';
import Contact from './components/Contact';
import { motion, useScroll, useSpring } from "framer-motion";

const App = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ container: ref });
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 80,
    damping: 30,
    restDelta: 0.001
  });

  const [modeNuit, setModeNuit] = useState(true);
  const toggleMode = () => {
    setModeNuit(!modeNuit);
  }

  return (
    <div className={`container-1 scrollable-content ${modeNuit ? 'mode-nuit' : ''}`} ref={ref}>
      <Taskbar toggleMode={toggleMode} modeNuit={modeNuit}/>
      <motion.progressbar
        className={`progress_page ${modeNuit ? 'mode-nuit' : ''}`}
        style={{ scaleX }}
      />
      <div className={`container-2 ${modeNuit ? 'mode-nuit' : ''}`}>
        <Home/>
      </div>
      <div className={`container-2 ${modeNuit ? 'mode-nuit' : ''}`}>
        <Formations/>
      </div>
      <div className={`container-2 ${modeNuit ? 'mode-nuit' : ''}`}>
        <Competences modeNuit={!modeNuit}/>
      </div>
      <div className={`container-2 ${modeNuit ? 'mode-nuit' : ''}`}>
        <Realisations/>
      </div>
      <div className={`container-2 ${modeNuit ? 'mode-nuit' : ''}`}>
        <Apropos/>
      </div>
      <div className={`container-2 ${modeNuit ? 'mode-nuit' : ''}`}>
        <Contact/>
      </div>
    </div>
)};

export default App;
