import React, { useEffect, useRef } from 'react';
import {  CProgress, CProgressBar, CButton } from '@coreui/react'
import '../css/Competences.css';
import Comp_pic from '../assets/images/competence.png'

const Competences = () => {
    const buttonsRefs = useRef([]);
    const hoverInterval = useRef(null);

    useEffect(() => {
        const buttons = buttonsRefs.current;
        const totalButtons = buttons.length;

        hoverInterval.current = setInterval(() => {
          const currentIndex = buttons.findIndex(button => button.classList.contains('hovered'));
          const nextIndex = (currentIndex + 1) % totalButtons;

          buttons[currentIndex].classList.remove('hovered');
          buttons[nextIndex].classList.add('hovered');
        }, 1000); // Délai en millisecondes entre chaque survol

        return () => clearInterval(hoverInterval.current);
    }, []);    

    return (
        <div className="comp_container">
            <h2 className="comp_h2">Compétences</h2>
            <div className='comp_comp'>
                <CButton
                    color="light"
                    variant="outline"
                    onMouseEnter={() => setHoveredButtonIndex(0)}
                    className={hoveredButtonIndex === 0 ? 'hovered' : ''}
                >
                    Conception
                </CButton>
                <CButton
                    color="light"
                    variant="outline"
                    onMouseEnter={() => setHoveredButtonIndex(1)}
                    className={hoveredButtonIndex === 1 ? 'hovered' : ''}
                >
                    Méthodologie
                </CButton>
                <CButton
                    color="light"
                    variant="outline"
                    onMouseEnter={() => setHoveredButtonIndex(2)}
                    className={hoveredButtonIndex === 2 ? 'hovered' : ''}
                >
                    Front-End
                </CButton>
                <CButton
                    color="light"
                    variant="outline"
                    onMouseEnter={() => setHoveredButtonIndex(3)}
                    className={hoveredButtonIndex === 3 ? 'hovered' : ''}
                >
                    Back-End
                </CButton>
            </div>
            <div className='comp_item'>
                <CProgress className="comp_progress" color="success" value={10}>
                    <CProgressBar>25%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="info" value={50}>
                    <CProgressBar>50%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="warning" value={75}>
                    <CProgressBar>75%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="danger" value={100}>
                    <CProgressBar>100%</CProgressBar>
                </CProgress>
            </div>
            <img
                src={Comp_pic}
                alt="pic_comp !"
                style={{
                position: 'absolute', 
                top: '60%',           
                right: '28%',         
                transform: 'translateY(-60%)', 
                width: '25%',      
                height: 'auto',       
                }}
            />
        </div>
    );
};

export default Competences;
