import React, { useState, useEffect } from 'react';
import '../css/Contact.css';
import { CForm, CFormInput, CFormTextarea, CButton} from '@coreui/react';
import FloatingButtons from 'react-floating-buttons';
import linkedIn from '../assets/images/linkedin.png';
import whatsApp from '../assets/images/whatsapp.png';
import skype from '../assets/images/skype.png';
import facebook from '../assets/images/facebook.png';

const Contact = () => {

    const buttonsList = [
        { onClick: ()=> alert('LinkedIn'), src: linkedIn},
        { onClick: ()=> alert('WhatsApp'), src: whatsApp},
        { onClick: ()=> alert('Skype'), src: skype},
        { onClick: ()=> alert('Facebook'), src: facebook},
    ];

    const [buttonDimension, setButtonDimension] = useState(40); // Taille par défaut

    useEffect(() => {
        const handleResize = () => {
            // Calculer la nouvelle dimension en fonction de la taille de la fenêtre
            const newDimension = Math.min(window.innerWidth * 0.03, 40); // 3% de la largeur de la fenêtre ou 40 (maximum)
            setButtonDimension(newDimension);
        };

        // Écouter les événements de redimensionnement de fenêtre
        window.addEventListener('resize', handleResize);

        // Désinscription de l'écouteur d'événement lors du démontage du composant
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    return (
        <div className="cont_container">
            <h2 className="cont_h2">Contact</h2>
            <div className="cont_item">
                <CForm>
                    <CFormInput
                        type="email"
                        id="exampleFormControlInput1"
                        label={<span className="custom_adress">Adresse email</span>}
                        placeholder="adress@example.com"
                        style={{ backgroundColor:'gray', fontSize: '1vw' }}
                    />
                </CForm>
                <CForm style={{ marginTop: '2%', marginBottom: '2%'}}>
                    <CFormTextarea
                        id="exampleFormControlTextarea1"
                        label={<span className="custom_message">Votre message</span>}
                        rows={4}
                        style={{ backgroundColor:'gray', height: '20vh', resize: 'none' }}
                    />
                </CForm>
                <CButton style={{ float: 'right' }} color='success'>
                    Envoyer
                </CButton>
                <div className="cont_button">
                    <FloatingButtons 
                        className='floating-button'
                        buttonType='plus' 
                        dimension={buttonDimension} 
                        buttonsList={buttonsList}
                        top={'calc(4vh - 25px)'}  
                        direction="right" 
                    />
                </div>
            </div>
        </div>
    );
};
export default Contact;
