import React, { useEffect, useState } from 'react';
import './App.css';
import { motion, useAnimation, useViewportScroll } from "framer-motion";
import Taskbar from './components/TaskBar';

const App = () => {
  const controls = useAnimation();
  const { scrollY } = useViewportScroll();
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (scrollY.current >= window.innerHeight) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };

    const controlsHandler = () => {
      if (isVisible) {
        controls.start("visible");
      } else {
        controls.start("hidden");
      }
    };

    scrollY.onChange(() => handleScroll());
    controlsHandler();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [scrollY, isVisible]);

  const variants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 },
  };

  return (
    <div className={`container-1 scrollable-content`}>
      <Taskbar/>
      <motion.div
        className="progress-bar"
        style={{ scaleX }}
      />
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>home</h1>
      </motion.div>
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>Formations</h1>
      </motion.div>
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>Compétences</h1>
      </motion.div>
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>Réalisations</h1>
      </motion.div>
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>A propos</h1>
      </motion.div>
      <motion.div className="container-2"
        variants={variants}
        initial="hidden"
        animate={controls}
      >
        <h1>Contact</h1>
      </motion.div>
    </div>
  );
}

export default App;
