import React, { useRef } from 'react';
import './App.css';
import Taskbar from './components/TaskBar';
import { motion, useScroll, useSpring, LazyMotion, domAnimation, m } from "framer-motion";

const App = ({ isVisible }) => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ container: ref });
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  return (
    <div className={`container-1 scrollable-content`} ref={ref}>
      <Taskbar/>
      <motion.div
        className="progress-bar"
        style={{ scaleX }}
      />
      <LazyMotion features={domAnimation}>
        <m.div className="container-2">
          <h1>home</h1>
        </m.div>
        <m.div className="container-2">
          <h1>Formations</h1>
        </m.div>
        <m.div className="container-2">
          <h1>Compétences</h1>
        </m.div>
        <m.div className="container-2">
          <h1>Réalisations</h1>
        </m.div>
        <m.div className="container-2">
          <h1>A propos</h1>
        </m.div>
        <m.div className="container-2">
          <h1>Contact</h1>
        </m.div>
      </LazyMotion>
    </div>
)};

export default App;
