import React from 'react';
import './App.css';
import Taskbar from './components/TaskBar';
import { motion, useScroll } from "framer-motion";

const App = () => {
  const { scrollYProgress } = useScroll();

  return (
    <>
    <div className={`container-1 scrollable-content`}>
      <Taskbar/>
      <motion.div
        className="progress-bar"
        style={{ scaleX: scrollYProgress }}
      />
      <div className="container-2">
        <h1>home</h1>
      </div>
      <div className="container-2">
        <h1>Formations</h1>
      </div>
      <div className="container-2">
        <h1>Compétences</h1>
      </div>
      <div className="container-2">
        <h1>Réalisations</h1>
      </div>
      <div className="container-2">
        <h1>A propos</h1>
      </div>
      <div className="container-2">
        <h1>Contact</h1>
      </div>
    </div>
    </>
)};

export default App;
