import React, { useRef, useEffect } from 'react';
import './App.css';
import Taskbar from './components/TaskBar';
import { motion, useScroll, useSpring } from "framer-motion";

const App = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({ container: ref });
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  // Fonction de défilement avec durée personnalisée
  const scrollTo = (element, to, duration) => {
    if (duration <= 0) return;
    const difference = to - element.scrollTop;
    const perTick = difference / duration * 10;

    setTimeout(() => {
      element.scrollTop = element.scrollTop + perTick;
      if (element.scrollTop === to) return;
      scrollTo(element, to, duration - 10);
    }, 10);
  };

  useEffect(() => {
    const container = ref.current;
    if (container) {
      // Appel de la fonction de défilement avec une durée de 1000 millisecondes
      scrollTo(container, 500, 1000); // Par exemple, faire défiler jusqu'à 500 pixels en 1000 millisecondes
    }
  }, []);

  return (
    <div className={`container-1 scrollable-content`} ref={ref}>
      <Taskbar/>
      <motion.div
        className="progress-bar"
        style={{ scaleX }}
      />
      <div className="container-2">
        <h1>home</h1>
      </div>
      <div className="container-2">
        <h1>Formations</h1>
      </div>
      <div className="container-2">
        <h1>Compétences</h1>
      </div>
      <div className="container-2">
        <h1>Réalisations</h1>
      </div>
      <div className="container-2">
        <h1>A propos</h1>
      </div>
      <div className="container-2">
        <h1>Contact</h1>
      </div>
    </div>
  );
};

export default App;
