import React from 'react';
import '../css/Contact.css';
import { CForm, CFormInput, CFormTextarea, CButton } from '@coreui/react';
import CIcon from '@coreui/icons-react'
import { cilSend, cibWhatsapp } from '@coreui/icons';
//import { SocialIcon } from 'react-social-icons';
import 'react-social-icons/linkedin'
import 'react-social-icons/google'
import 'react-social-icons/whatsapp'
import 'react-social-icons/facebook'

const Contact = () => {

    return (
        <div className="cont_container">
            <div className="div_text">
                <h2 className="cont_h2">Contact</h2>
                <p className="cont_text">
                    Ce CForm est pour la déco.<br/>
                    Mais vous pouvez me contacter parmis ces quatres icons.
                </p>
                <p className="cont_copie">
                    Handcrafted by Don Mariot R. © 2024
                </p>
            </div>
            <div className="cont_item">
                <CForm>
                    <CFormInput
                        type="email"
                        id="exampleFormControlInput1"
                        label={<span className="custom_adress">Email</span>}
                        placeholder="adresse@example.com"
                        style={{ backgroundColor:'gray', fontSize: '1vw' }}
                    />
                </CForm>
                <CForm style={{ marginTop: '2%', marginBottom: '2%'}}>
                    <CFormTextarea
                        id="exampleFormControlTextarea1"
                        label={<span className="custom_message">Message</span>}
                        rows={4}
                        style={{ backgroundColor:'gray', height: '20vh', resize: 'none' }}
                    />
                </CForm>
                <CButton style={{ float: 'right', width:'40px' }} color='success'>
                    <CIcon icon={cilSend}/>
                </CButton>
                {/* <SocialIcon style={{ width:'2.5vw', height:'5vh' }} 
                    href='https://www.linkedin.com/in/don-mariot-rakotozandry-198474210' 
                    url="www.linkedin.com" />
                <SocialIcon style={{ width:'2.5vw', height:'5.2vh', marginLeft:'1%' }} 
                    href='https://mail.google.com/mail/?view=cm&fs=1&to=razandri17@gmail.com' 
                    url="www.google.com" />
                <SocialIcon style={{ width:'2.5vw', height:'5vh', marginLeft:'1%' }} 
                    href='https://wa.me/261349706843' 
                    url="www.whatsapp.com" />
                <SocialIcon style={{ width:'2.5vw', height:'5vh', marginLeft:'1%' }} 
                    href='https://web.facebook.com/domariot/' 
                    url="www.facebook.com" /> */}
                <CIcon icon={cilSend}  size="sm"/>
                <CIcon icon={cibWhatsapp} className="text-success" size="sm"/>
                <CIcon icon={cilSend}  size="sm"/>
                <CIcon icon={cilSend}  size="sm"/>
                <CIcon icon={cilSend}  size="sm"/>    
            </div>
        </div>
    );
};
export default Contact;
