import React from 'react';
import '../css/Contact.css';
import { CForm, CFormInput, CFormTextarea, CButton} from '@coreui/react';
import FloatingButtons from 'react-floating-buttons';

const Contact = () => {

    const buttonsList = [
        { onClick: ()=> alert('LinkedIn')},
        { onClick: ()=> alert('WhatsApp')},
        { onClick: ()=> alert('Skype')},
        { onClick: ()=> alert('Facebook')},
    ];

      // Sélectionnez le conteneur et le bouton flottant
    const contItem = document.querySelector('.cont_item');
    const floatingButton = document.querySelector('.floating-button');

    // Fonction pour mettre à jour la position du bouton flottant
    function updateFloatingButtonPosition() {
        const contItemRect = contItem.getBoundingClientRect();
        const contItemTop = contItemRect.top + window.scrollY;
        const contItemLeft = contItemRect.left + window.scrollX;

        // Mise à jour des propriétés left et top du bouton flottant
        floatingButton.style.left = `${contItemLeft}px`;
        floatingButton.style.top = `${contItemTop}px`;
    }

    // Mettre à jour la position initiale du bouton flottant
    updateFloatingButtonPosition();

    // Écouter les événements de redimensionnement et de défilement de la fenêtre
    window.addEventListener('resize', updateFloatingButtonPosition);
    window.addEventListener('scroll', updateFloatingButtonPosition);

    return (
        <div className="cont_container">
            <h2 className="cont_h2">Contact</h2>
            <div className="cont_item">
                <CForm>
                    <CFormInput
                        type="email"
                        id="exampleFormControlInput1"
                        label={<span className="custom_adress">Adresse email</span>}
                        placeholder="adress@example.com"
                        style={{ backgroundColor:'gray', fontSize: '1vw' }}
                    />
                </CForm>
                <CForm style={{ marginTop: '2%', marginBottom: '2%'}}>
                    <CFormTextarea
                        id="exampleFormControlTextarea1"
                        label={<span className="custom_message">Votre message</span>}
                        rows={4}
                        style={{ backgroundColor:'gray', height: '20vh', resize: 'none' }}
                    />
                </CForm>
                <CButton style={{ float: 'right' }} color='success'>
                    Envoyer
                </CButton>
                <FloatingButtons 
                    buttonType='plus' 
                    dimension={36} 
                    distance={70}
                    buttonsList={buttonsList}
                    left={'34%'}  
                    top={'calc(52vh - 25px)'}  
                    direction="right" 
                />
            </div>
        </div>
    );
};
export default Contact;
