import React from 'react';
import { Button } from 'react-bootstrap';
import FancyText from '@carefully-coded/react-text-gradient';
import '../css/Home.css';
import Home_pic from '../assets/images/hello.png';

const Home = () => {
  return (
    <div className="home_container">
      <img
        src={Home_pic}
        alt="hello !"
        style={{
          float: 'left',
          marginRight: '20px', // Juste pour créer un espace entre l'image et le texte
          width: '200px', // Ajustez la largeur selon vos besoins
          height: 'auto', // Ajustez la hauteur automatiquement
        }}
      />
      <div>
        <h1 className="home_h1">
          <FancyText
            gradient={{ from: '#F858E0', to: '#77156C', type: 'linear' }}
            animateTo={{ from: '#6DEDD0', to: '#7AE23A' }}
            animateDuration={2000}
          >
            Hello !
          </FancyText>
        </h1>
        <p className="home_name">C'est Don Mariot</p>
        <p className="home_text" style={{ textAlign: 'justify' }}>
          Développeur fullstack passionné par l'innovation, je mets mon expertise technique au service de la création d'applications performantes et intuitives.
        </p>
        <Button className='home_btn' variant='outline-info' size='sm'>
          Me contacter
        </Button>
      </div>
    </div>
  );
};

export default Home;
