import React, { useRef, useEffect } from 'react';
import {  CProgress, CProgressBar, CButton } from '@coreui/react'
import '../css/Competences.css';
import Comp_pic from '../assets/images/competence.png'

const Competences = () => {
    const buttonRef = useRef(null);

    useEffect(() => {
        const button = buttonRef.current;

        const simulateHover = () => {
          button.classList.add("hover");
        };

        // Déclencher l'effet hover automatiquement avec un délai de 1 seconde
        const hoverTimeout = setTimeout(simulateHover, 5000);

        // Nettoyer l'effet hover une fois que le composant est démonté
        return () => {
          clearTimeout(hoverTimeout); // Nettoyer le timeout
          button.classList.remove("hover");
        };
      }, []);

    return (
        <div className="comp_container">
            <h2 className="comp_h2">Compétences</h2>
            <div className='comp_comp'>
                <CButton ref={buttonRef}  color="light" variant="outline">Conception</CButton>
                <CButton  color="light" variant="outline">Méthodologie</CButton>
                <CButton  color="light" variant="outline">Front-End</CButton>
                <CButton  color="light" variant="outline">Back-End</CButton>
            </div>
            <div className='comp_item'>
                <CProgress className="comp_progress" color="success" value={10}>
                    <CProgressBar>25%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="info" value={50}>
                    <CProgressBar>50%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="warning" value={75}>
                    <CProgressBar>75%</CProgressBar>
                </CProgress>
                <CProgress className="comp_progress" color="danger" value={100}>
                    <CProgressBar>100%</CProgressBar>
                </CProgress>
            </div>
            <img
                src={Comp_pic}
                alt="pic_comp !"
                style={{
                position: 'absolute', 
                top: '60%',           
                right: '28%',         
                transform: 'translateY(-60%)', 
                width: '25%',      
                height: 'auto',       
                }}
            />
        </div>
    );
};

export default Competences;
