import React, { useRef, useEffect } from 'react';
import './App.css';
import Taskbar from './components/TaskBar';
import { motion, useScroll, useSpring } from "framer-motion";

const App = () => {
  const ref = useRef(null);
  const { scrollY } = useScroll({ container: ref });
  const scaleX = useSpring(scrollY, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001
  });

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = ref.current.scrollTop;
      const containerHeight = ref.current.clientHeight;
      const childNodes = ref.current.children;
      let nextDivIndex = -1;

      for (let i = 0; i < childNodes.length; i++) {
        const childTop = childNodes[i].offsetTop;
        const childBottom = childTop + childNodes[i].offsetHeight;
        if (scrollTop >= childTop && scrollTop < childBottom) {
          nextDivIndex = i;
          break;
        }
      }

      if (nextDivIndex !== -1 && nextDivIndex < childNodes.length - 1) {
        const nextDivTop = childNodes[nextDivIndex + 1].offsetTop;
        ref.current.scrollTo({
          top: nextDivTop,
          behavior: 'smooth'
        });
      }
    };

    ref.current.addEventListener('scroll', handleScroll);

    return () => {
      ref.current.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className={`container-1 scrollable-content`} ref={ref}>
      <Taskbar/>
      <motion.div
        className="progress-bar"
        style={{ scaleX }}
      />
      <div className="container-2">
        <h1>Home</h1>
      </div>
      <div className="container-2">
        <h1>Formations</h1>
      </div>
      <div className="container-2">
        <h1>Compétences</h1>
      </div>
      <div className="container-2">
        <h1>Réalisations</h1>
      </div>
      <div className="container-2">
        <h1>A propos</h1>
      </div>
      <div className="container-2">
        <h1>Contact</h1>
      </div>
    </div>
  );
};

export default App;
